import React, { Component } from 'react';
import './App.css';
import './css/bootstrap.min.css';

class App extends React.Component {

  //function
  constructor(props) {
    super(props)

    this.state = {
       mahasiswa : [],
       nama : null,
       nim : null,
       nilai : null
    }
  }
  setValueState(event){
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  addData(){
    var data_tmp = this.state.mahasiswa;
    data_tmp.push({nim : this.state.nim, nama :
    this.state.nama, nilai : this.state.nilai});
    this.setState({
      mahasiswa : data_tmp
    })
  }

  render() {
    return (
       <div className="container">
         <div className="form-container">
           <div className="form-group">
             <label>Nim :</label>
             <input type="text" name="nim" value=
             {this.state.nim} onChange={this.
             setValueState.bind(this)}
             className="form-control"></input>
           </div>
           <div className="form-group">
             <label>Nama :</label>
             <input type="text" value=
             {this.state.nama} onChange={this.
             setValueState.bind(this)}
             className="form-control"></input>
           </div>

           <div className="form-group">
             <label>Nilai :</label>
             <input type="number" name="nilai" min="0"  value=
             {this.state.nilai} onChange={this.
             setValueState.bind(this)}
             className="form-control"></input>
           </div>
           <button onClick={this.addData.bind(this)} className="btn btn-primary mt-2">
              Submit
           </button>
         </div> <br />
         {/* {cetak}        */}
         <table className="table table-borderless">
           <thead>
             <tr>
               <th>No.</th>
               <th>Nim</th>
               <th>Nama</th>
               <th>Nilai</th>
             </tr>
           </thead>
           <tbody>
             {this.state.mahasiswa.map((mhs, index) =>(
             <tr key={index}>
               <td>{index+1}</td>
               <td>{mhs.nim}</td>
               <td>{mhs.nama}</td>
               <td>{mhs.nilai}</td>
              </tr>
             ))}
           </tbody>
         </table>
       </div>
    );
