import React, { Component } from 'react';
import './App.css';
import './css/bootstrap.min.css';

class App extends React.Component {
  render() { 
    var angka = [1,2,3];
    var nama = ["Budi", "Ali", "Susi"]
    var campuran = ["Budi", 1, "Ali", 2, "Susi", 3]
    return(
      <div>
        Isi array angka :
        <ul>
          {angka.map((item, index) =>(
            <li>Indeks ke - {index} : {item} </li>
          ))}
        </ul>
        Isi array nama :
        <ul>
          {nama.map((item, index) =>(
            <li>Indeks ke - {index} : {item} </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default App;
