import React from "react";
import "./App.css";
import "./css/bootstrap.min.css";
import {
  BrowserRouter as Router,
  Link,
  Route,
  Switch,
  Redirect,
  withRouter,
} from "react-router-dom";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isAuth: false,
    };
  }

  render() {
    const LoginButton = withRouter(({ history }) => (
      <button
        onClick={() => {
          this.setState({ isAuth: true });
          history.push("/profile");
        }}>
        Login
      </button>
    ));

    const LogoutButton = withRouter(({ history }) => (
      <button
        onClick={() => {
          this.setState({ isAuth: true });
          history.push("/login");
        }}>
        Logout
      </button>
    ));

    const routes = [
      {
        path: "/",
        exact: true,
        render: () => <div>Ini Halaman Home</div>,
      },
      {
        path: "/news",
        render: () => <div>Ini Halaman News</div>,
      },
      {
        path: "/login",
      },
    ];

    return (
      <Router>
        <div>
          <ul style={{ listStyle: "none" }}>
            <li>
              <Link to='/'>Home</Link>
            </li>
            <li>
              <Link to='/news'>News</Link>
            </li>
            <li>
              <Link to='/profile'>Profile</Link>
            </li>
          </ul>

          <Switch>
            <Route path='/' exact render={() => <div>Ini Halaman Home</div>} />
            <Route
              path='/news'
              exact
              render={() => <div>Ini Halaman News</div>}
            />
            <Route path='/login' exact render={() => <LoginButton />} />
            <Route
              path='/profile'
              exact
              render={() =>
                this.state.isAuth ? (
                  <div>
                    Ini Halaman Profile <br /> <LogoutButton />
                  </div>
                ) : (
                  <Redirect to='/login' />
                )
              }
            />
          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
